      }
  });

  var invalidPerson = {
      name: '',
      age: 'hello'
  };
  validatePerson(invalidPerson); //> {name: ['is required'], age: ['is not a number']}

  var validPerson = {
      name: 'Juan',
      age: 35
  };
  validatePerson(validPerson); //> undefined
  ```

