		value: false
	},
	executed: {
		type: "boolean",
		value: false
	},
	rejected: "any",
	get ready(){
		throw new Error("Transaction::ready must be provided by extended type");
	},
	get state() {
		if (this.rejected) {
			return "rejected";
		}
		if (this.executed) {
			return "executed";
		}
		if (this.executing) {
			return "executing";
		}
		// make sure there’s an amount, account, and card
		if (this.ready) {
			return "ready";
		}
		return "invalid";
	},
	executeStart: function(){
		throw new Error("Transaction::executeStart must be provided by extended type");
	},
	executeEnd: function(){
		throw new Error("Transaction::executeEnd must be provided by extended type");
	},
	execute: function() {
		if (this.state === "ready") {

			this.executing = true;

			var def = this.executeStart(),
				self = this;

			def.then(function() {
				can.batch.start();
				self.set({
					executing: false,
					executed: true
				});
				self.executeEnd();
				can.batch.stop();
			}, function(reason){
				self.set({
					executing: false,
					executed: true,
					rejected: reason
				});
			});
		}
	}
});

var Deposit = Transaction.extend({
	amount: "number",
	get ready() {
		return this.amount > 0 &&
			this.account &&
