    {{/case}}

    {{#case  "printingReceipt"}}
    	<h2>Printing Receipt</h2>
    {{/case}}

    {{#default}}
        <h2>Error</h2>
        <p>Invalid state - {{state}}</p>
    {{/default}}

{{/switch}}

		</div>
	</div>
</div>
</script>

